CREATE DATABASE TaskManager
COLLATE Latin1_General_100_CI_AS_SC_UTF8;

USE TaskManager;

CREATE TABLE dbo.User_System (ID_User INTEGER IDENTITY(1000,500) PRIMARY KEY
                              ,NM_User VARCHAR(45) COLLATE Latin1_General_100_CI_AS_SC_UTF8 NOT NULL
                              ,Email_User VARCHAR(80) COLLATE Latin1_General_100_CI_AS_SC_UTF8 NOT NULL
                              ,CHECK (Email_User LIKE '%_@__%.__%')
                              ,DT_Birth DATE
                              ,User_Gender VARCHAR(10) DEFAULT 'Others'
                              ,Password_User BINARY(64) NOT NULL
                              ,Avatar_User VARCHAR(MAX) DEFAULT '');

CREATE TABLE dbo.Task (ID_Task INTEGER IDENTITY(100,50) PRIMARY KEY
                      ,Description_Task NVARCHAR(80) COLLATE Latin1_General_100_CI_AS_SC_UTF8
                      ,DT_Created DATETIME
                      ,DT_Change DATETIME NULL
                      ,ID_User INTEGER NULL
                      ,FOREIGN KEY (ID_User) REFERENCES dbo.User_System(ID_User))